/**
 * Ethers.js fallback loader
 * This script will check if ethers is loaded and if not, load it from a CDN
 */

(function() {
    // Check if ethers is already loaded
    if (typeof window.ethers !== 'undefined') {
        console.log('Ethers.js is already loaded');
        return;
    }

    console.log('Loading ethers.js from CDN...');
    
    // Create a script element
    var script = document.createElement('script');
    // Try jsDelivr CDN instead
    script.src = 'https://cdn.jsdelivr.net/npm/ethers@5.7.2/dist/ethers.umd.min.js';
    script.type = 'text/javascript';
    script.async = true;
    
    // Add error handling
    script.onerror = function() {
        console.error('Failed to load ethers.js from CDN');
        // Try alternative CDN as backup
        var backupScript = document.createElement('script');
        backupScript.src = 'https://unpkg.com/ethers@5.7.2/dist/ethers.umd.min.js';
        backupScript.type = 'text/javascript';
        
        backupScript.onload = function() {
            console.log('Ethers.js loaded successfully from backup CDN');
            var event = new Event('ethers_loaded');
            document.dispatchEvent(event);
        };
        
        backupScript.onerror = function() {
            console.error('Failed to load ethers.js from backup CDN');
            alert('Failed to load required Ethereum libraries. Please try refreshing the page or contact support.');
        };
        
        document.head.appendChild(backupScript);
    };
    
    // Add load event handler
    script.onload = function() {
        console.log('Ethers.js loaded successfully');
        // Dispatch an event to notify that ethers is loaded
        var event = new Event('ethers_loaded');
        document.dispatchEvent(event);
    };
    
    // Append the script to the document
    document.head.appendChild(script);
})(); 